const MovieOnTopCard = (props) => {
    const ImagePath = "https://image.tmdb.org/t/p/w1280/" + props.posterImg;
    return (
        <div className="masterImage">
            <img src={ImagePath} alt="Main Poster"/>
            <style jsx>{`
                .masterImage {
                    text-align: center;
                    width: 80%;
                }
            `}
            </style>
        </div>
    )
}

export default MovieOnTopCard
