import React from 'react'
import Head from 'next/head'
import Nav from '../components/nav'
import Link from 'next/link'
import fetch from 'isomorphic-unfetch'
import Layout from '../components/layout'

const Home = ({data}) => (
  <div>
	<Layout>
		<div className='main'>
			<div className='row'>
				<div>
					<h2> Movie Category 1</h2>
					{/* <p>Total Pages : totalPages</p> */}
					<div>
						{data.map((value , i)=>{
							return (
								<div className="movieWrapper">
                  <div>
                    <img src={"https://image.tmdb.org/t/p/w300/" + value.poster_path} alt="Movie Image"/>
                  </div>
									<Link href="/movie/[pid]" as={{pathname: "/movie/" + value.id , query : {name : 'fordemo'}}} prefetch={false}>
										<a className="name"> {(value.original_title).slice(0, 10)} </a>
									</Link>
								</div>
							)
						})}
					</div>
				</div>			
			</div>
		</div>
	</Layout>
    <style jsx>{`
	.main {
		marign :10px;
		border:1px solid grey;
		text-align:  center;
	}
	.movieWrapper {
    display: inline-block;
    vertical-align: top;
		border: 1px solid grey;
		min-width: 23%;
		min-height: 300px;
		overfllow: hidden;
    text-align: center;
    background: black;
    min-height: 473px;
  }
  .name {
    color: white;
    text-decoration : none;
  }
    `}</style>
  </div>
)

Home.getInitialProps = async ({req}) => {
	const res = await fetch('https://api.themoviedb.org/3/movie/popular?api_key=c18a8c63bee9d66665a486a624d48177&language=en-US&page=1')
	const json = await res.json()
	console.log(json)
	return {data : json.results}
}



