const MovieOnTopCard = (props) => {
    const ImagePath = "https://image.tmdb.org/t/p/w780/" + props.posterImg;
    return (
        <div className="masterImage">
            <img src={ImagePath} alt="Main Poster"/>
            <style jsx>{`
                .masterImage {
                    text-align: center;
                    margin: 0 auto;
                    background: #141414;
                }
                .masterImage img {
                    width: 100%;
                }
            `}
            </style>
        </div>
    )
}

export default MovieOnTopCard
